    <script>
    function mercatorToLatLng(x, y) {
        var R = 6378137.0;
        var lon = x / R * 180 / Math.PI;
        var lat = (2 * Math.atan(Math.exp(y / R)) - Math.PI / 2) * 180 / Math.PI;
        return [lat, lon];
    }

