        const maps = {};
        const layers = [{"type":"wms","title":"Laax","layer_id":"9","wmsUrl":"\/mproxy\/service","layers":"Bees.Fields,Bees.Apiary","content":"<p style=\"margin-top: 0.5em; margin-bottom: 1em; color: rgb(32, 33, 34); font-family: sans-serif;\"><b>Laax<\/b>&nbsp;(<span class=\"IPA-label IPA-label-small\" style=\"font-size: 13.6px;\">German:<\/span>&nbsp;<span class=\"IPA nowrap\" lang=\"de-Latn-fonipa\" style=\"text-wrap-mode: nowrap;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/Help:IPA\/Standard_German\" title=\"Help:IPA\/Standard German\" style=\"color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word; text-decoration-line: none !important;\">[la\u02d0ks]<\/a><\/span>;&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Romansh_language\" title=\"Romansh language\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">Romansh<\/a>:&nbsp;<i lang=\"rm\">Lags<\/i>,&nbsp;<span class=\"IPA-label IPA-label-small\" style=\"font-size: 13.6px;\">pronounced<\/span>&nbsp;<span class=\"IPA nowrap\" lang=\"rm-Latn-fonipa\" style=\"text-wrap-mode: nowrap;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/Help:IPA\/Romansh\" title=\"Help:IPA\/Romansh\" style=\"color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word; text-decoration-line: none !important;\">[laks]<\/a><\/span>) is a&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Municipalities_of_Switzerland\" title=\"Municipalities of Switzerland\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">municipality<\/a>&nbsp;in the&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Surselva_Region\" title=\"Surselva Region\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">Surselva Region<\/a>&nbsp;in the&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Switzerland\" title=\"Switzerland\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">Swiss<\/a>&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Cantons_of_Switzerland\" title=\"Cantons of Switzerland\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">canton<\/a>&nbsp;of&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Graub%C3%BCnden\" class=\"mw-redirect\" title=\"Graub\u00fcnden\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">Graub\u00fcnden<\/a>.<\/p><div class=\"mw-heading mw-heading2\" style=\"color: rgb(16, 20, 24); margin-top: 0.25em; margin-bottom: 0.25em; padding-top: 0.5em; padding-bottom: 0.17em; display: flow-root; word-break: break-word; border-bottom: 1px solid rgb(162, 169, 177); font-size: 1.5em; font-family: &quot;Linux Libertine&quot;, Georgia, Times, &quot;Source Serif Pro&quot;, serif; line-height: 1.375;\"><h2 id=\"History\" style=\"color: inherit; font-weight: inherit; margin-bottom: 0.25em; display: inline; word-break: break-word; border: 0px; font-size: inherit; font-style: inherit; font-variant: inherit; font-stretch: inherit; line-height: 1.375; font-optical-sizing: inherit; font-size-adjust: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit;\">History<\/h2><span class=\"mw-editsection\" style=\"user-select: none; font-size: small; margin-left: 1em; margin-inline: 1em 0px; vertical-align: baseline; line-height: 0; unicode-bidi: isolate; font-family: sans-serif;\"><span class=\"mw-editsection-bracket\" style=\"color: rgb(84, 89, 93); margin-right: 0.25em; margin-inline: 0px 0.25em;\">[<\/span><a href=\"https:\/\/en.wikipedia.org\/w\/index.php?title=Laax&amp;action=edit&amp;section=1\" title=\"Edit section: History\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; text-wrap-mode: nowrap; overflow-wrap: break-word;\">edit<\/a><span class=\"mw-editsection-bracket\" style=\"color: rgb(84, 89, 93); margin-left: 0.25em; margin-inline: 0.25em 0px;\">]<\/span><\/span><\/div><figure class=\"mw-default-size\" typeof=\"mw:File\/Thumb\" style=\"margin-top: 0.5em; margin-bottom: 1.3em; margin-left: 1.4em; display: table; text-align: center; border-collapse: collapse; line-height: 0; clear: right; float: right; border-width: 1px 1px 0px; border-top-style: solid; border-top-color: rgb(200, 204, 209); border-right-style: solid; border-right-color: rgb(200, 204, 209); border-left-style: solid; border-left-color: rgb(200, 204, 209); border-image: none 100% \/ 1 \/ 0 stretch; border-bottom-style: initial; border-bottom-color: initial; background-color: rgb(248, 249, 250); color: rgb(32, 33, 34); min-width: 100px; font-family: sans-serif;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/File:Siegel_Freie_von_Laax.JPG\" class=\"mw-file-description\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word; display: block; position: relative; border: 0px;\"><img src=\"https:\/\/upload.wikimedia.org\/wikipedia\/commons\/thumb\/8\/89\/Siegel_Freie_von_Laax.JPG\/250px-Siegel_Freie_von_Laax.JPG\" decoding=\"async\" width=\"250\" height=\"247\" class=\"mw-file-element\" srcset=\"\/\/upload.wikimedia.org\/wikipedia\/commons\/thumb\/8\/89\/Siegel_Freie_von_Laax.JPG\/500px-Siegel_Freie_von_Laax.JPG 1.5x\" data-file-width=\"1264\" data-file-height=\"1250\" style=\"border-width: 1px; border-style: solid; border-color: rgb(200, 204, 209); border-image: none 100% \/ 1 \/ 0 stretch; margin: 3px; background-color: rgb(255, 255, 255); color: rgb(32, 33, 34); height: auto; max-width: calc(100% - 8px);\"><\/a><figcaption style=\"display: table-caption; caption-side: bottom; background-color: inherit; line-height: 1.4em; word-break: break-word; text-align: start; padding-right: 6px; padding-bottom: 6px; padding-left: 6px; border-width: 0px 1px 1px; border-right-style: solid; border-right-color: rgb(200, 204, 209); border-bottom-style: solid; border-bottom-color: rgb(200, 204, 209); border-left-style: solid; border-left-color: rgb(200, 204, 209); border-image: none 100% \/ 1 \/ 0 stretch; border-top-style: initial; border-top-color: initial; font-size: 14.144px;\">Seal of the \"Freie von Laax\", 1727<\/figcaption><\/figure><table class=\"box-Expand_section plainlinks metadata ambox mbox-small-left ambox-content\" role=\"presentation\" style=\"font-size: 14.08px; border-width: 1px 1px 1px 10px; border-style: solid; border-color: rgb(162, 169, 177) rgb(162, 169, 177) rgb(162, 169, 177) rgb(242, 133, 0); border-image: initial; background-color: rgb(251, 251, 251); margin-top: 4px; margin-right: 1em; margin-bottom: 4px; overflow: hidden; width: 238px; line-height: 1.25em; color: rgb(32, 33, 34); font-family: sans-serif;\"><tbody><tr><td class=\"mbox-image\" style=\"border: none; padding-top: 2px; padding-bottom: 2px; padding-left: 0.5em; text-align: center;\"><span typeof=\"mw:File\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/File:Wiki_letter_w_cropped.svg\" class=\"mw-file-description\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><img alt=\"[icon]\" src=\"https:\/\/upload.wikimedia.org\/wikipedia\/commons\/thumb\/1\/1c\/Wiki_letter_w_cropped.svg\/20px-Wiki_letter_w_cropped.svg.png\" decoding=\"async\" width=\"20\" height=\"14\" class=\"mw-file-element\" srcset=\"\/\/upload.wikimedia.org\/wikipedia\/commons\/thumb\/1\/1c\/Wiki_letter_w_cropped.svg\/40px-Wiki_letter_w_cropped.svg.png 1.5x\" data-file-width=\"44\" data-file-height=\"31\" style=\"border: 0px;\"><\/a><\/span><\/td><td class=\"mbox-text\" style=\"border: none; padding: 0.25em 0.5em; width: 185.906px;\"><div class=\"mbox-text-span\">This section&nbsp;<b>needs expansion<\/b>. You can help by&nbsp;<a class=\"external text\" href=\"https:\/\/en.wikipedia.org\/w\/index.php?title=Laax&amp;action=edit&amp;section=\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); border-radius: 2px; overflow-wrap: break-word; background: none !important;\">adding to it<\/a>.&nbsp;<span class=\"date-container\"><i>(<span class=\"date\">March 2022<\/span>)<\/i><\/span><\/div><\/td><\/tr><\/tbody><\/table><p style=\"margin-top: 0.5em; margin-bottom: 1em; color: rgb(32, 33, 34); font-family: sans-serif;\">Laax is first undoubtedly mentioned&nbsp;<abbr title=\"circa\" style=\"border-bottom: 0px;\">c.<\/abbr>&nbsp;1290\u201398 as&nbsp;<i>Lags<\/i>.<sup id=\"cite_ref-HDS_3-0\" class=\"reference\" style=\"line-height: 1; unicode-bidi: isolate; text-wrap-mode: nowrap; font-size: 12.8px;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/Laax#cite_note-HDS-3\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><span class=\"cite-bracket\" style=\"pointer-events: none;\">[<\/span>3<span class=\"cite-bracket\" style=\"pointer-events: none;\">]<\/span><\/a><\/sup><\/p><p style=\"margin-top: 0.5em; margin-bottom: 1em; color: rgb(32, 33, 34); font-family: sans-serif;\">Laax is first mentioned indirectly in 765 AD when Bishop&nbsp;<a href=\"https:\/\/en.wikipedia.org\/w\/index.php?title=Tello_of_Chur&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Tello of Chur (page does not exist)\" style=\"text-decoration-line: none; color: rgb(191, 60, 44); background: none; border-radius: 2px; overflow-wrap: break-word;\">Tello of Chur<\/a>&nbsp;granted his share of Flemme (<a href=\"https:\/\/en.wikipedia.org\/wiki\/Flims\" title=\"Flims\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">Flims<\/a>) to the&nbsp;<a href=\"https:\/\/en.wikipedia.org\/w\/index.php?title=Disentis_Monastery&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Disentis Monastery (page does not exist)\" style=\"text-decoration-line: none; color: rgb(191, 60, 44); background: none; border-radius: 2px; overflow-wrap: break-word;\">Disentis Monastery<\/a>, noting Laax in his will. The village appears in a 1290 register of the Cathedral of Chur and was officially independent by 1525 with its own parish church. In 1428, the&nbsp;<a href=\"https:\/\/de.wikipedia.org\/wiki\/Freie_von_Laax\" class=\"extiw\" title=\"de:Freie von Laax\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">de:Freie von Laax<\/a>&nbsp;bought their freedom from Count[&nbsp;<a href=\"https:\/\/en.wikipedia.org\/w\/index.php?title=Rudolf_VII_of_Werdenberg-Sargans&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Rudolf VII of Werdenberg-Sargans (page does not exist)\" style=\"text-decoration-line: none; color: rgb(191, 60, 44); background: none; border-radius: 2px; overflow-wrap: break-word;\">Rudolf VII of Werdenberg-Sargans<\/a>, gaining unique autonomy, freedom, and privileges such as market rights and tolls. In 1677,&nbsp;<a href=\"https:\/\/en.wikipedia.org\/w\/index.php?title=Udalrich_de_Mont&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Udalrich de Mont (page does not exist)\" style=\"text-decoration-line: none; color: rgb(191, 60, 44); background: none; border-radius: 2px; overflow-wrap: break-word;\">Udalrich de Mont<\/a>,&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Bishop_of_Chur\" title=\"Bishop of Chur\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">Bishop of Chur<\/a>, consecrated the new church in Laax to the patrons&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Saint_Gallus\" class=\"mw-redirect\" title=\"Saint Gallus\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">Saint Gallus<\/a>&nbsp;and&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Saint_Otmar\" class=\"mw-redirect\" title=\"Saint Otmar\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">Saint Otmar<\/a>. Laax joined the&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Ilanz\" title=\"Ilanz\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">Ilanz<\/a>&nbsp;district in 1851 after the dissolution of the&nbsp;<a href=\"https:\/\/en.wikipedia.org\/w\/index.php?title=Laax-Seewis&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Laax-Seewis (page does not exist)\" style=\"text-decoration-line: none; color: rgb(191, 60, 44); background: none; border-radius: 2px; overflow-wrap: break-word;\">Laax-Seewis<\/a>&nbsp;court, and tourism began developing in 1962 with the opening of the Crap Sogn Gion.<\/p>","map_center":"46.808108291240735,9.253578186035158","map_zoom":"16","url":"\/mproxy\/service","name":"Bees.Fields,Bees.Apiary"},{"type":"html","title":"Bee Farming Project","content":"<h1 id=\"sample-projects\" style=\"box-sizing: inherit; color: rgba(0, 0, 0, 0.54); font-size: 2em; line-height: 1.3; margin-bottom: 1.25em; letter-spacing: -0.01em; font-family: Roboto, -apple-system, BlinkMacSystemFont, Helvetica, Arial, sans-serif;\"><span style=\"color: rgba(0, 0, 0, 0.87); font-size: 17.6px;\">QField ships with sample projects by default. You can use these projects to explore some of the QField configuration details in QGIS and QFieldSync.<\/span><\/h1><p style=\"box-sizing: inherit; color: rgba(0, 0, 0, 0.87); font-family: Roboto, -apple-system, BlinkMacSystemFont, Helvetica, Arial, sans-serif; font-size: 17.6px;\">These sample projects demonstrate various field data collection workflows and QField features:<\/p><ul style=\"box-sizing: inherit; margin-bottom: 1em; margin-top: 1em; list-style-type: disc; padding-left: 0px; margin-left: 0.625em; display: flow-root; color: rgba(0, 0, 0, 0.87); font-family: Roboto, -apple-system, BlinkMacSystemFont, Helvetica, Arial, sans-serif; font-size: 17.6px;\"><li style=\"box-sizing: inherit; margin-bottom: 0.5em; margin-left: 1.25em;\">The Bee Farming project showcases form-based data entry and spatial data collection<\/li><li style=\"box-sizing: inherit; margin-bottom: 0px; margin-left: 1.25em;\">The Wastewater Management project illustrates infrastructure mapping and inspection workflows<\/li><\/ul><p style=\"box-sizing: inherit; color: rgba(0, 0, 0, 0.87); font-family: Roboto, -apple-system, BlinkMacSystemFont, Helvetica, Arial, sans-serif; font-size: 17.6px;\">Click the buttons below to download the sample project archives:<\/p><p style=\"box-sizing: inherit; color: rgba(0, 0, 0, 0.87); font-family: Roboto, -apple-system, BlinkMacSystemFont, Helvetica, Arial, sans-serif; font-size: 17.6px;\"><a class=\"md-button\" href=\"https:\/\/docs.qfield.org\/assets\/projects\/bee_farming_project.zip\" style=\"box-sizing: inherit; -webkit-tap-highlight-color: transparent; color: rgb(139, 195, 75); text-decoration-line: none; word-break: break-word; transition: color 125ms, background-color 125ms, border-color 125ms; border: 0.1rem solid; border-radius: 0.1rem; cursor: pointer; display: inline-block; font-weight: 700; padding: 0.625em 2em;\">Bee Farming Sample Project&nbsp;<span class=\"twemoji\" style=\"box-sizing: inherit; --md-icon-size: 1.125em; display: inline-flex; height: 19.8px; vertical-align: text-top;\"><svg xmlns=\"http:\/\/www.w3.org\/2000\/svg\" viewBox=\"0 0 24 24\"><path d=\"M17.4 9C17 7.8 16.2 7 15 6.5V5h-1v1.4h-.4c-1.1 0-2 .4-2.8 1.2l-.4.4L9 7.5c-.3-.1-.6-.2-1-.2-.6 0-1.2.2-1.7.6-.6.4-.9.9-1.1 1.4-.2.7-.2 1.3 0 2 .3.7.6 1.2 1.1 1.5-.4 1.5-.1 2.8 1 3.9.8.8 1.7 1.2 2.8 1.2.5 0 .8 0 1.1-.1.6.8 1.4 1.3 2.4 1.3.3 0 .7 0 1-.1.6-.2 1-.6 1.4-1.1.4-.6.6-1.1.6-1.7 0-.4 0-.7-.1-1l-.5-1.6.6-.4c.8-.8 1.2-1.9 1.1-3.1H19V9h-1.6m-9.7 2.3c-.6-.3-.8-.7-.6-1.3.2-.6.6-.8 1.2-.6l3.2 1.2c-1.6.8-2.8 1-3.8.7m6.3 5.6c-.6.2-1 0-1.3-.6-.3-1-.1-2.2.7-3.8l1.2 3.1c.2.7 0 1.1-.6 1.3m1.2-5.3-.6-1.6v-.1l-.3-.3h-.1L12.6 9c.4-.3.8-.5 1.3-.5s1 .2 1.4.6c.4.4.6.8.6 1.3-.2.3-.4.8-.7 1.2Z\"><\/path><\/svg><\/span><\/a>&nbsp;<a class=\"md-button\" href=\"https:\/\/docs.qfield.org\/assets\/projects\/wastewater_management_project.zip\" style=\"box-sizing: inherit; -webkit-tap-highlight-color: transparent; color: rgb(139, 195, 75); text-decoration-line: none; word-break: break-word; transition: color 125ms, background-color 125ms, border-color 125ms; border: 0.1rem solid; border-radius: 0.1rem; cursor: pointer; display: inline-block; font-weight: 700; padding: 0.625em 2em;\">Wastewater Management Sample Project&nbsp;<span class=\"twemoji\" style=\"box-sizing: inherit; --md-icon-size: 1.125em; display: inline-flex; height: 19.8px; vertical-align: text-top;\"><svg xmlns=\"http:\/\/www.w3.org\/2000\/svg\" viewBox=\"0 0 24 24\"><path d=\"M11.6 13v-.1l-.3-.3h-.1L9.6 12c.4-.3.8-.5 1.3-.5s1 .2 1.4.6c.4.4.6.8.6 1.3s-.1.9-.5 1.3l-.8-1.7m-1.9 6.3c-.3-1-.1-2.2.7-3.8l1.2 3.1c.2.6 0 1-.6 1.3-.6.3-1 .1-1.3-.6m-5.6-6.2c.2-.6.6-.8 1.2-.6l3.2 1.2c-1.6.8-2.8 1-3.8.7-.6-.3-.8-.7-.6-1.3m7.9-5h-1v1.4h-.4c-1.1 0-2 .4-2.8 1.2l-.4.6-1.4-.8c-.3-.1-.6-.1-1-.1-.6 0-1.2.2-1.7.6s-.9.8-1.1 1.4c-.2.7-.2 1.3 0 2 .3.7.6 1.2 1.1 1.5-.4 1.5-.1 2.8 1 3.9.8.8 1.7 1.2 2.8 1.2.5 0 .8 0 1.1-.1.6.8 1.4 1.3 2.4 1.3.3 0 .7 0 1-.1.6-.2 1-.6 1.4-1.1.4-.6.6-1.1.6-1.7 0-.4 0-.7-.1-1l-.6-1.4.6-.4c.8-.8 1.2-1.9 1.1-3.1H16v-1h-1.6c-.4-1.2-1.2-2-2.4-2.4V8.1m5.3-1.3c-.2-.2-.3-.5-.3-.7 0-.3.1-.5.3-.7.2-.2.4-.3.7-.3s.5.1.7.3c.2.1.3.4.3.7 0 .3-.1.5-.3.7-.2.2-.4.2-.7.2s-.5 0-.7-.2m3.4-2.7h-1.1l-.3-.9c-.2-.7-.6-1-1.3-1-.7 0-1.2.3-1.3 1l-.3.9h-1.1c-.6 0-1 .3-1.3.9-.2.6 0 1.1.6 1.5l.9.5-.4 1.2c-.2.4-.1.8.1 1.2.3.4.6.6 1.1.6.4 0 .7-.1.9-.3l.8-.6.8.7c.2.1.5.2.9.2.5 0 .8-.2 1.1-.6.2-.4.3-.8.1-1.2L20.5 7l.8-.5c.6-.4.8-.9.6-1.5-.2-.7-.6-.9-1.2-.9Z\"><\/path><\/svg><\/span><\/a><\/p><p style=\"box-sizing: inherit; color: rgba(0, 0, 0, 0.87); font-family: Roboto, -apple-system, BlinkMacSystemFont, Helvetica, Arial, sans-serif; font-size: 17.6px;\">You can also explore the sample projects directly in the&nbsp;<a href=\"https:\/\/github.com\/opengisch\/QField\/tree\/master\/resources\/sample_projects\" style=\"box-sizing: inherit; -webkit-tap-highlight-color: transparent; color: rgb(114, 173, 46); text-decoration-line: none; word-break: break-word; transition: color 125ms;\">QField GitHub repository<\/a>.<\/p>"},{"type":"wms","title":"Tracks","layer_id":"9","wmsUrl":"\/mproxy\/service","layers":"Bees.Tracks","content":"<div class=\"mw-heading mw-heading2\" style=\"color: rgb(16, 20, 24); margin-top: 0.25em; margin-bottom: 0.25em; padding-top: 0.5em; padding-bottom: 0.17em; display: flow-root; word-break: break-word; border-bottom: 1px solid rgb(162, 169, 177); font-size: 1.5em; font-family: &quot;Linux Libertine&quot;, Georgia, Times, &quot;Source Serif Pro&quot;, serif; line-height: 1.375;\"><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 16px;\">Laax has a population (as of 31 December 2020) of 1,974.<\/span><sup id=\"cite_ref-Stat2020_GR_6-0\" class=\"reference\" style=\"color: rgb(32, 33, 34); font-family: sans-serif; line-height: 1; unicode-bidi: isolate; text-wrap-mode: nowrap; font-size: 12.8px;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/Laax#cite_note-Stat2020_GR-6\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><span class=\"cite-bracket\" style=\"pointer-events: none;\">[<\/span>6<span class=\"cite-bracket\" style=\"pointer-events: none;\">]<\/span><\/a><\/sup><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 16px;\">&nbsp;As of 2008, 16.8% of the population was made up of foreign nationals.<\/span><sup id=\"cite_ref-Population_7-0\" class=\"reference\" style=\"color: rgb(32, 33, 34); font-family: sans-serif; line-height: 1; unicode-bidi: isolate; text-wrap-mode: nowrap; font-size: 12.8px;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/Laax#cite_note-Population-7\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><span class=\"cite-bracket\" style=\"pointer-events: none;\">[<\/span>7<span class=\"cite-bracket\" style=\"pointer-events: none;\">]<\/span><\/a><\/sup><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 16px;\">&nbsp;Over the last 10 years the population has grown at a rate of 5.8%. Most of the population (as of 2000) speaks German (50.3%), with&nbsp;<\/span><a href=\"https:\/\/en.wikipedia.org\/wiki\/Romansh_language\" title=\"Romansh language\" style=\"font-family: sans-serif; background: none; font-size: 16px; text-decoration-line: none; color: rgb(51, 102, 204); border-radius: 2px; overflow-wrap: break-word;\">Romansh<\/a><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 16px;\">&nbsp;being second most common (40.1%) and Portuguese being third ( 3.9%).<\/span><sup id=\"cite_ref-SFSO_4-1\" class=\"reference\" style=\"color: rgb(32, 33, 34); font-family: sans-serif; line-height: 1; unicode-bidi: isolate; text-wrap-mode: nowrap; font-size: 12.8px;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/Laax#cite_note-SFSO-4\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><span class=\"cite-bracket\" style=\"pointer-events: none;\">[<\/span>4<span class=\"cite-bracket\" style=\"pointer-events: none;\">]<\/span><\/a><\/sup><\/div><p style=\"margin-top: 0.5em; margin-bottom: 1em; color: rgb(32, 33, 34); font-family: sans-serif;\">As of 2000, the gender distribution of the population was 51.1% male and 48.9% female.<sup id=\"cite_ref-GR_Numbers_8-0\" class=\"reference\" style=\"line-height: 1; unicode-bidi: isolate; text-wrap-mode: nowrap; font-size: 12.8px;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/Laax#cite_note-GR_Numbers-8\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><span class=\"cite-bracket\" style=\"pointer-events: none;\">[<\/span>8<span class=\"cite-bracket\" style=\"pointer-events: none;\">]<\/span><\/a><\/sup>&nbsp;The age distribution, as of 2000, in Laax is; 102 children or 8.9% of the population are between 0 and 9 years old and 147 teenagers or 12.8% are between 10 and 19. Of the adult population, 152 people or 13.2% of the population are between 20 and 29 years old. 212 people or 18.4% are between 30 and 39, 171 people or 14.9% are between 40 and 49, and 184 people or 16.0% are between 50 and 59. The senior population distribution is 110 people or 9.6% of the population are between 60 and 69 years old, 49 people or 4.3% are between 70 and 79, there are 23 people or 2.0% who are between 80 and 89.<sup id=\"cite_ref-Population_7-1\" class=\"reference\" style=\"line-height: 1; unicode-bidi: isolate; text-wrap-mode: nowrap; font-size: 12.8px;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/Laax#cite_note-Population-7\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><span class=\"cite-bracket\" style=\"pointer-events: none;\">[<\/span>7<span class=\"cite-bracket\" style=\"pointer-events: none;\">]<\/span><\/a><\/sup><\/p><p style=\"margin-top: 0.5em; margin-bottom: 1em; color: rgb(32, 33, 34); font-family: sans-serif;\">In the 2011&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/2011_Swiss_federal_election\" title=\"2011 Swiss federal election\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">last federal election<\/a>&nbsp;the most popular party was the&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Christian_Democratic_People%27s_Party_of_Switzerland\" title=\"Christian Democratic People's Party of Switzerland\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">CVP<\/a>&nbsp;which received 26.9% of the vote. The next three most popular parties were the&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Swiss_People%27s_Party\" title=\"Swiss People's Party\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">SVP<\/a>&nbsp;(24.5%), the&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Conservative_Democratic_Party_of_Switzerland\" title=\"Conservative Democratic Party of Switzerland\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">BDP<\/a>&nbsp;(18.3%), and the&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Free_Democratic_Party_of_Switzerland\" title=\"Free Democratic Party of Switzerland\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">FDP<\/a>&nbsp;(13.3%).<sup id=\"cite_ref-SFSO_4-2\" class=\"reference\" style=\"line-height: 1; unicode-bidi: isolate; text-wrap-mode: nowrap; font-size: 12.8px;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/Laax#cite_note-SFSO-4\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><span class=\"cite-bracket\" style=\"pointer-events: none;\">[<\/span>4<span class=\"cite-bracket\" style=\"pointer-events: none;\">]<\/span><\/a><\/sup><\/p><p style=\"margin-top: 0.5em; margin-bottom: 1em; color: rgb(32, 33, 34); font-family: sans-serif;\">The entire Swiss population is generally well educated. In Laax about 74.6% of the population (between age 25\u201364) have completed either non-mandatory&nbsp;<a href=\"https:\/\/en.wikipedia.org\/wiki\/Education_in_Switzerland#Secondary\" title=\"Education in Switzerland\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">upper secondary education<\/a>&nbsp;or additional higher education (either university or a&nbsp;<i><a href=\"https:\/\/en.wikipedia.org\/wiki\/Fachhochschule\" title=\"Fachhochschule\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\">Fachhochschule<\/a><\/i>).<sup id=\"cite_ref-SFSO_4-3\" class=\"reference\" style=\"line-height: 1; unicode-bidi: isolate; text-wrap-mode: nowrap; font-size: 12.8px;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/Laax#cite_note-SFSO-4\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><span class=\"cite-bracket\" style=\"pointer-events: none;\">[<\/span>4<span class=\"cite-bracket\" style=\"pointer-events: none;\">]<\/span><\/a><\/sup><\/p><p style=\"margin-top: 0.5em; margin-bottom: 1em; color: rgb(32, 33, 34); font-family: sans-serif;\"><sup id=\"cite_ref-SFSO_4-3\" class=\"reference\" style=\"line-height: 1; unicode-bidi: isolate; text-wrap-mode: nowrap; font-size: 12.8px;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/Laax#cite_note-SFSO-4\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><span class=\"cite-bracket\" style=\"pointer-events: none;\"><br><\/span><\/a><\/sup><\/p><p style=\"margin-top: 0.5em; margin-bottom: 1em; color: rgb(32, 33, 34); font-family: sans-serif;\"><sup id=\"cite_ref-SFSO_4-3\" class=\"reference\" style=\"line-height: 1; unicode-bidi: isolate; text-wrap-mode: nowrap; font-size: 12.8px;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/Laax#cite_note-SFSO-4\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><span class=\"cite-bracket\" style=\"pointer-events: none;\"><\/span><\/a><\/sup><\/p><table class=\"infobox ib-settlement vcard\" style=\"font-size: 14.08px; border: 1px solid rgb(162, 169, 177); color: black; padding: 0.2em; line-height: 1.2em; border-spacing: 3px; background-color: rgb(248, 249, 250); margin-top: 0.5em; margin-bottom: 0.5em; margin-left: 1em; clear: right; width: 23em; font-family: sans-serif;\"><tbody><tr class=\"mergedtoprow\"><td colspan=\"2\" class=\"infobox-full-data\" style=\"vertical-align: top; text-align: center; border-top-width: 1px; border-top-color: rgb(162, 169, 177); padding: 0.4em 0.6em 0.2em;\"><span typeof=\"mw:File\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/File:Laax_See.jpg\" class=\"mw-file-description\" title=\"Laax in August 2008\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><img alt=\"Laax in August 2008\" src=\"https:\/\/upload.wikimedia.org\/wikipedia\/commons\/thumb\/3\/35\/Laax_See.jpg\/330px-Laax_See.jpg\" decoding=\"async\" width=\"265\" height=\"177\" class=\"mw-file-element\" srcset=\"\/\/upload.wikimedia.org\/wikipedia\/commons\/thumb\/3\/35\/Laax_See.jpg\/500px-Laax_See.jpg 1.5x, \/\/upload.wikimedia.org\/wikipedia\/commons\/thumb\/3\/35\/Laax_See.jpg\/960px-Laax_See.jpg 2x\" data-file-width=\"3456\" data-file-height=\"2304\" style=\"border: 0px;\"><\/a><\/span><div class=\"ib-settlement-caption\" style=\"padding-top: 0.3em;\">Laax in August 2008<\/div><\/td><\/tr><tr class=\"mergedtoprow\"><td colspan=\"2\" class=\"infobox-full-data maptable\" style=\"vertical-align: top; text-align: center; border-top-width: 1px; border-top-color: rgb(162, 169, 177); padding: 0.4em 0.6em 0.2em; border-right-style: initial; border-bottom-style: initial; border-left-style: initial; border-right-color: initial; border-bottom-color: initial; border-left-color: initial; border-image: initial;\"><div class=\"ib-settlement-cols\" style=\"display: table; width: 304.953px;\"><div class=\"ib-settlement-cols-row\" style=\"display: table-row;\"><div class=\"ib-settlement-cols-cell\" style=\"display: table-cell; vertical-align: middle;\"><span class=\"mw-image-border\" typeof=\"mw:File\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/File:CHE_Laax_Flag.svg\" class=\"mw-file-description\" title=\"Flag of Laax\/Lags\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><img alt=\"Flag of Laax\/Lags\" src=\"https:\/\/upload.wikimedia.org\/wikipedia\/commons\/thumb\/e\/e4\/CHE_Laax_Flag.svg\/60px-CHE_Laax_Flag.svg.png\" decoding=\"async\" width=\"60\" height=\"60\" class=\"mw-file-element\" srcset=\"\/\/upload.wikimedia.org\/wikipedia\/commons\/thumb\/e\/e4\/CHE_Laax_Flag.svg\/120px-CHE_Laax_Flag.svg.png 1.5x\" data-file-width=\"512\" data-file-height=\"514\" style=\"border: 1px solid rgb(234, 236, 240);\"><\/a><\/span><div class=\"ib-settlement-caption-link\" style=\"padding-top: 0.2em; padding-bottom: 0.2em;\">Flag<\/div><\/div><div class=\"ib-settlement-cols-cell\" style=\"display: table-cell; vertical-align: middle;\"><span typeof=\"mw:File\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/File:CHE_Laax_COA.svg\" class=\"mw-file-description\" title=\"Coat of arms of Laax\/Lags\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; overflow-wrap: break-word;\"><img alt=\"Coat of arms of Laax\/Lags\" src=\"https:\/\/upload.wikimedia.org\/wikipedia\/commons\/thumb\/d\/d2\/CHE_Laax_COA.svg\/60px-CHE_Laax_COA.svg.png\" decoding=\"async\" width=\"60\" height=\"70\" class=\"mw-file-element\" srcset=\"\/\/upload.wikimedia.org\/wikipedia\/commons\/thumb\/d\/d2\/CHE_Laax_COA.svg\/120px-CHE_Laax_COA.svg.png 1.5x\" data-file-width=\"227\" data-file-height=\"265\" style=\"border: 0px;\"><\/a><\/span><div class=\"ib-settlement-caption-link\" style=\"padding-top: 0.2em; padding-bottom: 0.2em;\">Coat of arms<\/div><\/div><\/div><\/div><\/td><\/tr><tr class=\"mergedtoprow\"><td colspan=\"2\" class=\"infobox-full-data\" style=\"vertical-align: top; text-align: center; border-top-width: 1px; border-top-color: rgb(162, 169, 177); padding: 0.4em 0.6em 0.2em;\"><div class=\"hidden-begin mw-collapsible mw-collapsed mw-made-collapsible\" style=\"width: 304.953px; padding: 5px; border: none; font-size: 13.376px;\"><button type=\"button\" class=\"mw-collapsible-toggle mw-collapsible-toggle-default mw-collapsible-toggle-collapsed\" aria-expanded=\"false\" tabindex=\"0\" style=\"float: right; user-select: none; appearance: none; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; padding-right: 0.2em; padding-left: 0.2em; border-width: 0px; border-style: initial; border-color: initial; font-style: inherit; font-variant: inherit; font-weight: normal; font-stretch: inherit; font-optical-sizing: inherit; font-size-adjust: inherit; font-kerning: inherit; font-feature-settings: inherit; font-variation-settings: inherit;\"><span class=\"mw-collapsible-text\" style=\"color: rgb(51, 102, 204); border-radius: 2px;\">show<\/span><\/button><div class=\"hidden-title skin-nightmode-reset-color\" style=\"font-weight: bold; line-height: 1.6; height: 5px;\">Location of Laax\/Lags<\/div><\/div><\/td><\/tr><tr class=\"mergedtoprow\"><td colspan=\"2\" class=\"infobox-full-data\" style=\"vertical-align: top; text-align: center; border-top-width: 1px; border-top-color: rgb(162, 169, 177); padding: 0.4em 0.6em 0.2em;\"><div class=\"switcher-container\"><div class=\"center\" style=\"width: 304.953px;\"><div class=\"locmap\" style=\"margin-left: auto; margin-right: auto; width: 250px; float: none; clear: both;\"><div style=\"margin-left: auto; margin-right: auto; width: 250px;\"><div style=\"margin-left: auto; margin-right: auto; position: relative; width: 250px;\"><span class=\"notpageimage\" typeof=\"mw:File\" style=\"margin-left: auto; margin-right: auto;\"><a href=\"https:\/\/en.wikipedia.org\/wiki\/File:Switzerland_adm_location_map.svg\" class=\"mw-file-description\" title=\"Laax\/Lags is located in Switzerland\" style=\"text-decoration-line: none; color: rgb(51, 102, 204); background: none; border-radius: 2px; margin-left: auto; margin-right: auto; overflow-wrap: break-word;\"><img alt=\"Laax\/Lags is located in Switzerland\" src=\"https:\/\/upload.wikimedia.org\/wikipedia\/commons\/thumb\/8\/83\/Switzerland_adm_location_map.svg\/250px-Switzerland_adm_location_map.svg.png\" decoding=\"async\" width=\"250\" height=\"160\" class=\"mw-file-element\" srcset=\"\/\/upload.wikimedia.org\/wikipedia\/commons\/thumb\/8\/83\/Switzerland_adm_location_map.svg\/500px-Switzerland_adm_location_map.svg.png 1.5x\" data-file-width=\"1347\" data-file-height=\"864\" style=\"border: 0px; margin-left: auto; margin-right: auto;\"><\/a><\/span><\/div><\/div><\/div><\/div><\/div><\/td><\/tr><\/tbody><\/table>","map_center":"46.81133924039194,9.252247810363771","map_zoom":"16","url":"\/mproxy\/service","name":"Bees.Tracks"}];
        const featureInfoPanel = document.getElementById('featureInfoPanel');
        const featureInfoContent = document.getElementById('featureInfoContent');
        const closeFeatureInfo = document.getElementById('closeFeatureInfo');
        
        console.log('DEBUG: Initializing maps with layers:', layers);
        
        if (closeFeatureInfo) {
            closeFeatureInfo.onclick = function() {
                featureInfoPanel.style.display = 'none';
            };
        }

        // For each section, initialize a map in its own container
        layers.forEach((section, index) => {
            console.log('DEBUG: Processing section', index, section);
            if (section.type === 'wms' || section.type === 'upload') {
                const mapId = `map-section-${index}`;
                const mapElement = document.getElementById(mapId);
                console.log('DEBUG: Found map element for', mapId, mapElement);
                
                if (mapElement) {
                    // Initialize map with proper settings
                    const map = L.map(mapId, {
                        zoomControl: false,
                        scrollWheelZoom: false,
                        dragging: true,
                        attributionControl: true
                    }).setView([0, 0], 2);

                    // Add base layer
                    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
                        attribution: '&copy; OpenStreetMap contributors'
                    }).addTo(map);

                    // Enable zoom only when Control key is pressed
                    map.on('wheel', function(e) {
                        if (e.originalEvent.ctrlKey) {
                            e.originalEvent.preventDefault();
                            if (e.originalEvent.deltaY < 0) {
                                map.zoomIn();
                            } else {
                                map.zoomOut();
                            }
                        }
                    });

                    if (section.type === 'wms') {
                        try {
                            const wmsUrl = section.url.replace(/^@+/, '').trim();
                            console.log('DEBUG: Adding WMS layer', wmsUrl, section.name);
                            
                            const wmsLayer = L.tileLayer.wms(wmsUrl + '<?=$access_key?>', {
                                layers: section.name,
                                format: 'image/png',
                                transparent: true,
                                identify: true
                            }).addTo(map);

                            console.log('DEBUG: Layer bounds:', section.bounds, 'CRS:', section.bounds && section.bounds.crs);
                            if (
                                section.bounds &&
                                typeof section.bounds.south === 'number' &&
                                typeof section.bounds.west === 'number' &&
                                typeof section.bounds.north === 'number' &&
                                typeof section.bounds.east === 'number'
                            ) {
                                if (section.bounds.crs === 'EPSG:3857') {
                                    const sw = mercatorToLatLng(section.bounds.west, section.bounds.south);
                                    const ne = mercatorToLatLng(section.bounds.east, section.bounds.north);
                                    map.fitBounds([sw, ne]);
                                } else {
                                    const bounds = [
                                        [section.bounds.south, section.bounds.west],
                                        [section.bounds.north, section.bounds.east]
                                    ];
                                    map.fitBounds(bounds);
                                }
                                setTimeout(function() {
                                    map.panBy([-524, 0], {animate: false});
                                }, 300);
                            } else if (section.map_center && section.map_zoom) {
                                const parts = section.map_center.split(',');
                                if (parts.length === 2) {
                                    const lat = parseFloat(parts[0]);
                                    const lng = parseFloat(parts[1]);
                                    const zoom = parseInt(section.map_zoom, 10);
                                    map.setView([lat, lng], zoom);
                                } else {
                                    map.setView([37.8, -96], 4);
                                }
                            } else {
                                map.setView([37.8, -96], 4); // fallback to US center/zoom
                            }

                            // Initialize layer content if it exists
                            const storySection = mapElement.closest('.story-section');
                            if (storySection) {
                                const layerContent = storySection.querySelector('.layer-content');
                                if (layerContent && section.content) {
                                    layerContent.innerHTML = section.content;
                                    layerContent.style.display = 'block';
                                }
                            }

                            // Add click handler for feature info
                            map.on('click', function(e) {
                                const point = map.latLngToContainerPoint(e.latlng, map.getZoom());
                                const size = map.getSize();
                                const bounds = map.getBounds();
                                const bbox = `${bounds.getSouth()},${bounds.getWest()},${bounds.getNorth()},${bounds.getEast()}`;
                                
                                const params = {
                                    REQUEST: 'GetFeatureInfo',
                                    SERVICE: 'WMS',
                                    CRS: 'EPSG:4326',
                                    STYLES: '',
                                    TRANSPARENT: true,
                                    VERSION: '1.3.0',
                                    FORMAT: 'image/png',
                                    BBOX: bbox,
                                    HEIGHT: size.y,
                                    WIDTH: size.x,
                                    LAYERS: section.name,
                                    QUERY_LAYERS: section.name,
                                    INFO_FORMAT: 'text/html',
                                    I: Math.round(point.x),
                                    J: Math.round(point.y),
                                    FEATURE_COUNT: 10
                                };

                                let baseUrl = wmsUrl;
                                if (baseUrl.endsWith('?') || baseUrl.endsWith('&')) {
                                    baseUrl = baseUrl.slice(0, -1);
                                }
                                const url = baseUrl + (baseUrl.includes('?') ? '&' : '?') + new URLSearchParams(params).toString();

                                fetch(url)
                                    .then(response => response.text())
                                    .then(html => {
                                        if (html && html.trim() !== '') {
                                            featureInfoContent.innerHTML = html;
                                            featureInfoPanel.style.display = 'block';
                                        }
                                    })
                                    .catch(error => {
                                        console.error('Error fetching feature info:', error);
                                    });
                            });

                            maps[mapId] = map;
                            console.log('DEBUG: Map initialized for', mapId);
                        } catch (error) {
                            console.error('Error adding WMS layer:', error);
                        }
                    } else if (section.type === 'upload') {
                        try {
                            console.log('Processing upload section:', section);
                            console.log('Section type:', section.type);
                            console.log('Section data:', JSON.stringify(section, null, 2));
                            
                            if (!section.geojson) {
                                console.error('No GeoJSON data found in section:', section);
                                throw new Error('No GeoJSON data found in upload section');
                            }
                            
                            const geojson = JSON.parse(section.geojson);
                            console.log('Parsed GeoJSON:', geojson.type, 'with', geojson.features?.length, 'features');
                            
                            if (!geojson.type || !geojson.features) {
                                throw new Error('Invalid GeoJSON structure');
                            }

                            const geojsonLayer = L.geoJSON(geojson, {
                                style: function(feature) {
                                    const geometry = feature.geometry.type;
                                    const style = {
                                        fillColor: section.style?.fillColor || '#3388ff',
                                        fillOpacity: section.style?.fillOpacity || 0.4,
                                        color: section.style?.strokeColor || '#000000',
                                        weight: section.style?.strokeWidth || 1
                                    };

                                    // Add point styling
                                    if (geometry === 'Point' || geometry === 'MultiPoint') {
                                        style.radius = section.style?.pointRadius || 5;
                                    }

                                    return style;
                                },
                                pointToLayer: function(feature, latlng) {
                                    const geometry = feature.geometry.type;
                                    const style = {
                                        fillColor: section.style?.fillColor || '#3388ff',
                                        fillOpacity: section.style?.fillOpacity || 0.4,
                                        color: section.style?.strokeColor || '#000000',
                                        weight: section.style?.strokeWidth || 1,
                                        radius: section.style?.pointRadius || 5
                                    };
                                    
                                    return L.circleMarker(latlng, {
                                        radius: style.radius,
                                        fillColor: style.fillColor,
                                        color: style.color,
                                        weight: style.weight,
                                        fillOpacity: style.fillOpacity
                                    });
                                },
                                onEachFeature: function(feature, layer) {
                                    if (feature.properties) {
                                        layer.on('click', function(e) {
                                            // Format the properties as HTML
                                            const content = Object.entries(feature.properties)
                                                .map(([key, value]) => `<div><strong>${key}:</strong> ${value}</div>`)
                                                .join('');
                                            
                                            featureInfoContent.innerHTML = content;
                                            featureInfoPanel.style.display = 'block';
                                        });
                                    }
                                }
                            }).addTo(map);

                            // Initialize layer content if it exists
                            const storySection = mapElement.closest('.story-section');
                            if (storySection) {
                                const layerContent = storySection.querySelector('.upload-content');
                                if (layerContent && section.content) {
                                    layerContent.innerHTML = section.content;
                                    layerContent.style.display = 'block';
                                }
                            }

                            // Fit map to the bounds of the GeoJSON
                            map.fitBounds(geojsonLayer.getBounds());
                            console.log('Upload layer added successfully');
                        } catch (error) {
                            console.error('Error processing upload:', error);
                            console.error('Section data:', section);
                        }
                    }
                } else {
                    console.error('Map element not found:', mapId);
                }
            }
        });

        // Initial resize to ensure maps are properly sized
        setTimeout(() => {
            Object.values(maps).forEach(map => {
                map.invalidateSize();
                map.setView(map.getCenter(), map.getZoom());
            });
        }, 500);

        // Handle window resize
        window.addEventListener('resize', () => {
            Object.values(maps).forEach(map => {
                map.invalidateSize();
                map.setView(map.getCenter(), map.getZoom());
            });
        });
    });
    </script>
</body>
</html>
